import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RoomsPage.css';

const RoomsPage = () => {
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    fetchRooms();
  }, []);

  const fetchRooms = async () => {
    try {
      const response = await axios.get('http://localhost:5000/rooms');
      setRooms(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке номеров:', error);
    }
  };

  return (
    <div className="rooms-container">
      <h1>Список номеров</h1>
      <div className="rooms-grid">
        {rooms.map((room) => (
          <div className="room-card" key={room.id}>
            <img
              src={`http://localhost:5000/images/${room.photo_url}`}
              alt={room.comfort_level}
              className="room-image"
            />
            <h3>{room.comfort_level}</h3>
            <p>Категория: {room.category_name}</p>
            <p>Вместимость: {room.capacity} человек</p>
            <p>Цена: {room.price.toFixed(2)} руб./ночь</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoomsPage;
