import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RoomsPage.css';

const RoomsPage = () => {
  const [rooms, setRooms] = useState([]);
  const [editingRoom, setEditingRoom] = useState(null);
  const [editedRoom, setEditedRoom] = useState(null);

  useEffect(() => {
    fetchRooms();
  }, []);

  // Получение списка номеров
  const fetchRooms = async () => {
    try {
      const response = await axios.get('http://localhost:5000/rooms');
      setRooms(response.data);
    } catch (error) {
      console.error('Ошибка при загрузке номеров:', error);
    }
  };

  // Обновление номера
  const updateRoom = async (id) => {
    try {
      await axios.put(`http://localhost:5000/rooms/${id}`, editedRoom);
      setEditingRoom(null);
      setEditedRoom(null);
      fetchRooms();
    } catch (error) {
      console.error('Ошибка при обновлении номера:', error);
    }
  };

  // Удаление номера
  const deleteRoom = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/rooms/${id}`);
      fetchRooms();
    } catch (error) {
      console.error('Ошибка при удалении номера:', error);
    }
  };

  // Отображение карточек
  return (
    <div className="rooms-container">
      <h1>Список номеров</h1>
      <div className="rooms-grid">
        {rooms.map((room) => (
          <div className="room-card" key={room.id}>
            {editingRoom === room.id ? (
              <>
                <input
                  type="text"
                  defaultValue={room.comfort_level}
                  onChange={(e) => setEditedRoom({ ...room, comfort_level: e.target.value })}
                />
                <input
                  type="number"
                  defaultValue={room.capacity}
                  onChange={(e) => setEditedRoom({ ...room, capacity: e.target.value })}
                />
                <input
                  type="number"
                  defaultValue={room.price}
                  onChange={(e) => setEditedRoom({ ...room, price: e.target.value })}
                />
                <button onClick={() => updateRoom(room.id)}>Сохранить</button>
                <button onClick={() => setEditingRoom(null)}>Отмена</button>
              </>
            ) : (
              <>
                <img
                  src={`http://localhost:5000/images/${room.photo_url}`}
                  alt={room.comfort_level}
                  className="room-image"
                />
                <h3>{room.comfort_level}</h3>
                <p>Категория: {room.category_name}</p>
                <p>Вместимость: {room.capacity} человек</p>
               <p>Цена: {parseFloat(room.price).toFixed(2)} руб./ночь</p>

                <div className="card-actions">
                  <button onClick={() => setEditingRoom(room.id)}>Редактировать</button>
                  <button onClick={() => deleteRoom(room.id)}>Удалить</button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default RoomsPage;
